import { useState } from "react"
import { TextField, Checkbox, Button, FormControlLabel, Box, Typography, Link } from "@mui/material"

function LoginForm() {
  const [loading, setLoading] = useState(false)
  const [formData, setFormData] = useState({
    healthCard: "",
    password: "",
    rememberMe: false,
  })

  const handleSubmit = async (e) => {
    e.preventDefault()
    setLoading(true)
    // Simulate API call
    setTimeout(() => {
      setLoading(false)
      console.log("Form submitted:", formData)
    }, 1000)
  }

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }))
  }

  return (
    <Box className="login-form-container" sx={{ maxWidth: 400, mx: "auto", padding: 2 }}>
      <Box className="form-header" mb={2}>
        <Typography variant="h4">Welcome Back</Typography>
        <Typography variant="body2">Enter your health card number and password to access your account</Typography>
      </Box>

      <form onSubmit={handleSubmit}>
        <TextField
          label="Health Card Number"
          type="text"
          id="healthCard"
          name="healthCard"
          fullWidth
          margin="normal"
          placeholder="Enter your health card number..."
          value={formData.healthCard}
          onChange={handleChange}
          required
        />

        <TextField
          label="Password"
          type="password"
          id="password"
          name="password"
          fullWidth
          margin="normal"
          value={formData.password}
          onChange={handleChange}
          required
        />

        <FormControlLabel
          control={
            <Checkbox
              id="rememberMe"
              name="rememberMe"
              checked={formData.rememberMe}
              onChange={handleChange}
            />
          }
          label="Remember me"
        />

        <Button
          type="submit"
          variant="contained"
          color="primary"
          fullWidth
          disabled={loading}
          sx={{ mt: 2 }}
        >
          {loading ? "Signing In..." : "Sign In"}
        </Button>
      </form>

      <Box className="create-account" mt={2} textAlign="center">
        <Typography variant="body2">Don't have an account? <Link href="/signup">Create an account</Link></Typography>
      </Box>
    </Box>
  )
}

export default LoginForm
