import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from './NavBar';

const LandingPage = () => {
  const navigate = useNavigate();
  const [selectedRole, setSelectedRole] = useState(null);

  const handleNext = () => {
    if (selectedRole === 'patient') {
      navigate('/p-login');
    } else if (selectedRole === 'practitioner') {
      navigate('/h-login');
    }
  };

  return (
    <div>
      <div>
        <header>
          <a href="/">MedSync</a>
          <Navbar />
        </header>
        <main>
          <div>
            <h1>Select your role</h1>
            <p>
              Welcome to MedSync, your integrated healthcare platform. Choose your role to
              access personalized features and services designed specifically for your needs.
            </p>
          </div>

          <div>
            <button
              onClick={() => setSelectedRole('patient')}
            >
              <div>
                <img 
                  src="/placeholder.svg?height=200&width=200" 
                  alt="Patient illustration"
                />
              </div>
              <h2>Patient Care</h2>
            </button>

            <button
              onClick={() => setSelectedRole('practitioner')}
            >
              <div>
                <img 
                  src="/placeholder.svg?height=200&width=200" 
                  alt="Healthcare practitioner illustration"
                />
              </div>
              <h2>Healthcare Practitioner</h2>
            </button>
          </div>

          <div>
            <button
              onClick={handleNext}
              disabled={!selectedRole}
            >
              Next
            </button>
          </div>
        </main>
      </div>
    </div>
  );
};

export default LandingPage;
